// src/WasteStationPage.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

function WasteStationPage() {
  const [stations, setStations] = useState([]);

  useEffect(() => {
    const fetchStations = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/waste_stations/');
        setStations(response.data);
      } catch (error) {
        console.error('Erro ao buscar as estações:', error);
      }
    };

    fetchStations();
  }, []);

  const handleVolumeChange = async (id, newVolume) => {
    try {
      await axios.patch(`http://127.0.0.1:8000/api/waste_stations/${id}/`, { volume_percentage: newVolume });
      setStations(stations.map(station => (station.id === id ? { ...station, volume_percentage: newVolume } : station)));
    } catch (error) {
      console.error('Erro ao atualizar o volume percentage:', error);
    }
  };

  return (
    <div>
      <h1>Waste Stations</h1>
      <ul>
        {stations.map(station => (
          <li key={station.id}>
            <p>{station.name}</p>
            <p>Volume Percentage: {station.volume_percentage}%</p>
            <input
              type="number"
              value={station.volume_percentage}
              onChange={(e) => handleVolumeChange(station.id, e.target.value)}
            />
          </li>
        ))}
      </ul>
    </div>
  );
}

export default WasteStationPage;
