import React, { useState, useEffect } from 'react';
import axios from 'axios';

const WasteStationPage = () => {
  const [stations, setStations] = useState([]);

  const fetchStations = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:8000/api/waste_stations/');
      setStations(response.data);
    } catch (error) {
      console.error('Erro ao buscar as estações:', error);
    }
  };

  const handleCollectRequest = async (id) => {
    try {
      await axios.patch(`http://127.0.0.1:8000/api/waste_stations/${id}/`, {
        collection_requested: true,
      });
      fetchStations();  // Atualiza a lista de estações após a solicitação de coleta
    } catch (error) {
      console.error('Erro ao solicitar a coleta:', error);
    }
  };

  const handleCollectConfirmation = async (id) => {
    try {
      await axios.patch(`http://127.0.0.1:8000/api/waste_stations/${id}/`, {
        collection_requested: false,
        collection_confirmed: true,
        volume_percentage: 0,  // Zera o volume após a confirmação da coleta
      });
      fetchStations();  // Atualiza a lista de estações após a confirmação da coleta
    } catch (error) {
      console.error('Erro ao confirmar a coleta:', error);
    }
  };

  useEffect(() => {
    fetchStations();
  }, []);

  return (
    <div>
      <h1>Waste Stations</h1>
      <ul>
        {stations.map((station) => (
          <li key={station.id}>
            {station.name} - {station.volume_percentage}% Volume
            {station.collection_requested && !station.collection_confirmed && (
              <button onClick={() => handleCollectConfirmation(station.id)}>
                Confirmar Coleta
              </button>
            )}
            {!station.collection_requested && (
              <button onClick={() => handleCollectRequest(station.id)}>
                Solicitar Coleta
              </button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default WasteStationPage;
